const processTracks=t=>{const e=document.querySelector(".tracks");e.innerHTML="";const a=t.map((t=>{let e="https://soundcloud.com/aiso/"+t.url;return`<div class="track">\n\t\t\t<a rel="noopener nofollow" href="${t.url}" target="_blank" aria-label="${t.title}"><img alt="${t.title}" sizes="(max-width: 500px) 500px, 300px" src="/images/cover/cover-${t.cover}-300x300.jpg" srcset="/images/cover/cover-${t.cover}.jpg 500w, /images/cover/cover-${t.cover}-300x300.jpg 300w" loading="lazy"></a>\n\t\t\t<p class="date">${t.date}</p>\n\t\t\t<p class="tags">${t.tags.join(", ")}</p>\n\t\t\t<h3><a rel="noopener nofollow" href="${e}" target="_blank" aria-label="${t.title}">${t.title}</a></h3>\n\t\t</div>`})).join("");e.innerHTML=a};document.addEventListener("DOMContentLoaded",(t=>{fetch("/js/tracks.json").then((t=>t.json())).then((t=>{processTracks(t.tracks)}))}));